mapper.select:
select
  com_id, com_name, com_purch_price, com_price, com_num, com_description, com_volume, sor_id, gmt_create, gmt_modified, by_sp1, by_sp2
from web-shop.commodity


        and com_id = #{comId}


        and com_name = #{comName}


        and com_purch_price = #{comPurchPrice}


        and com_price = #{comPrice}


        and com_num = #{comNum}


        and com_description = #{comDescription}


        and com_volume = #{comVolume}


        and sor_id = #{sorId}


        and gmt_create = #{gmtCreate}


        and gmt_modified = #{gmtModified}


        and by_sp1 = #{bySp1}


        and by_sp2 = #{bySp2}
