mapper.select:
select
  ord_id, con_id, loc_id, ord_total_price, ord_status, ord_remark, gmt_create, gmt_modified, by_sp1, by_sp2
from web-shop.orders


        and ord_id = #{ordId}


        and con_id = #{conId}


        and loc_id = #{locId}


        and ord_total_price = #{ordTotalPrice}


        and ord_status = #{ordStatus}


        and ord_remark = #{ordRemark}


        and gmt_create = #{gmtCreate}


        and gmt_modified = #{gmtModified}


        and by_sp1 = #{bySp1}


        and by_sp2 = #{bySp2}
